import './App.css';
import styled from 'styled-components';

const SearchResult = styled.div`
`;

function App() {
    const [searchKey, setSearchKey]=useState("Search");
    const ApiKey ="&key=AIzaSyA-O0PnAoDduuz490vJ286BDvDLKt08fHE";
    const UtubeLink = `https://youtube.googleapis.com/youtube/v3/search?part=snippet&q=${searchKey}${ApiKey}`
    let Show = false;

    const [input, setInput] = useState('');

    const getInput = (e) => {
        const keyInput = e.target.value;
        setInput(keyInput);
    }

    // const getSearchKey = (e) => {
    //     const keyInput = e.target.value;
    //     const searchKey = keyInput;
    //     // === "" ? "Search" : keyInput;
    //     setSearchKey(searchKey);
    // }
    // console.log(searchKey);

    const letSearch = (e) => {
        if (e.key === "Enter" || e.code === "NumpadEnter") {
            console.log(UtubeLink); 
            setSearchKey(input);
            // Show = true;
            // console.log(Show); 
            // getSearchKey(e);
        }
    }


    const [result, setResult] = useState([]);

    useEffect(() => {
        fetch(UtubeLink)
        .then(res => res.json())
        .then(posts => {
            setResult(posts);
        })
    },[searchKey]);

    return (
        <div className="App">
            <input type="text" name="themeSize" placeholder={input} onChange={getInput} onKeyPress={letSearch}></input>
            <h1>{searchKey}</h1>
            {/* <p>{result}</p> */}
        </div>

    );
}

export default App;
