import React, {useState, useEffect} from 'react';
import './App.css';
import styled from 'styled-components';
import ReactPlayer from 'react-player'
import parse from "html-react-parser";


const SearchResult = styled.div`
display: flex;
justify-content: center;
flex-direction: column;
align-items: center;
`;
const Items = styled.div`
display: flex;
flex-direction: column;
padding: 50px;
width: 50%;
// align-items: center;
margin-left: 50px auto;
    button {
        display: flex;
        align-items: center;
        background: white;
        border: solid 1px white;
    }
    p {
        padding-left: 20px;
    }
// :hover {
//     color: green;
//     border: solid 1px green;
// }
`;

function App() {
    const [searchKey, setSearchKey]=useState('');
    const [result, setResult] = useState('');
    // const [result, setResult] = useState({items:[]});
    const [loading, setLoading] = useState(false);
    const [input, setInput] = useState("Search");
    const watchVideo = "https://www.youtube.com/watch?v=";

    const ApiKey ="&key=AIzaSyA-O0PnAoDduuz490vJ286BDvDLKt08fHE";
    const UtubeLink = `https://youtube.googleapis.com/youtube/v3/search?part=snippet&q=${searchKey}&maxResults=25${ApiKey}`
    let Show = false;

    const getInput = (e) => {
        const input = e.target.value;
        const newInput = input === "" ? "Search" : input;
        setInput(newInput);
    }

    const letSearch = (e) => {
        if (e.key === "Enter" || e.code === "NumpadEnter") {
            console.log(UtubeLink); 
            setSearchKey(input);
        }
    }



    useEffect(() => {
        setLoading(true);
        fetch(UtubeLink)
        .then(res => res.json())
        .then(posts => {
            setResult(posts);
            setLoading(false);
            setVideo("https://www.youtube.com/embed/"+posts.items[0].id.videoId);
            // console.log(video);
        })
    },[searchKey]);

    const playVideo = (e) => {
        const linkVideo = e.target.value;
        setVideo(linkVideo);
        console.log(video);
    }

    const [video,setVideo] = useState('https://www.youtube.com/embed/1So7VBehCQg');


    return (
        <div className="App">
            <h1>Tìm kiếm video trên youtube</h1>
            <input type="text" name="themeSize" placeholder={input} onChange={getInput} onKeyPress={letSearch}></input>
            <h1>Kết quả cho từ khóa "{searchKey}"</h1>
            {/* {result && <iframe className="player" type="text/html" width="800px" height="600px" src={"https://www.youtube.com/embed/"+result.items[0].id.videoId} frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen/>} */}
            {result && <iframe className="player" type="text/html" width="800px" height="600px" src={video} frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen/>}

            <SearchResult>

            {loading && (<div>Loading...</div>)}
            {result && (
            <Items>
                {result.items.map(item => (
                    <button value={"https://www.youtube.com/embed/" + item.id.videoId} type="button" onClick={playVideo} key={item.id.videoId} >
                        <img src={item.snippet.thumbnails.default.url}/>
                        <p>{item.snippet.title}</p>
                    </button>
                ))}
            </Items>
            )}
            </SearchResult>
        </div>

    );
}

export default App;
