import React, {useState, useEffect} from 'react';
import './App.css';
import styled from 'styled-components';

const SearchResult = styled.div`
`;

function App() {
    const [searchKey, setSearchKey]=useState("Search");
    const ApiKey ="&key=AIzaSyA-O0PnAoDduuz490vJ286BDvDLKt08fHE";
    const UtubeLink = `https://youtube.googleapis.com/youtube/v3/search?part=${searchKey}${ApiKey}`
    let Show = false;

    console.log(searchKey);


    const getSearchKey = (e) => {
        const keyInput = e.target.value;
        const searchKey = keyInput === "" ? "Search" : keyInput;
        setSearchKey(searchKey);
    }

    const letSearch = (e) => {
        if (e.key === "Enter" || e.code === "NumpadEnter") {
            console.log(UtubeLink); 
            Show = true;
            console.log(Show); 
        }
    }


    const [result, setResearch] = useState([]);

    useEffect(() => {
        fetch(UtubeLink)
        .then(res => res.json())
        .then(posts => {
            setResearch(posts);
        })
    },[searchKey]);

    return (
        <div className="App">
            <input type="text" name="themeSize" placeholder={searchKey} onChange={getSearchKey} onKeyPress={letSearch}></input>
            <h1>{searchKey}</h1>
            <p>{result}</p>
        </div>

    );
}

export default App;
