import React, {useState, useEffect} from 'react';
import './App.css';
import styled from 'styled-components';
import ReactPlayer from 'react-player'


const SearchResult = styled.div`
display: flex;
justify-content: center;
`;
const Items = styled.div`
display: flex;
padding: 5px;
align-items: center;
margin-left: 5px auto;
p {
    padding-left: 20px;
}
`;

function App() {
    const [searchKey, setSearchKey]=useState('');
    const [result, setResult] = useState({items:[]});
    const [loading, setLoading] = useState(false);
    const [input, setInput] = useState("Search");
    const watchVideo = "https://www.youtube.com/watch?v=";

    const ApiKey ="&key=AIzaSyAYrj_BQjiJwfo7JSR_eRMSbPAtX4Q57tE";
    const UtubeLink = `https://youtube.googleapis.com/youtube/v3/search?part=snippet&q=${searchKey}&maxResults=25${ApiKey}`
    let Show = false;

    const getInput = (e) => {
        const input = e.target.value;
        const newInput = input === "" ? "Search" : input;
        setInput(newInput);
    }

    const letSearch = (e) => {
        if (e.key === "Enter" || e.code === "NumpadEnter") {
            console.log(UtubeLink); 
            setSearchKey(input);
        }
    }



    useEffect(() => {
        setLoading(true);
        fetch(UtubeLink)
        .then(res => res.json())
        .then(posts => {
            setResult(posts);
            console.log(result);
            setLoading(false);
        })
    },[searchKey]);



    return (
        <div className="App">
            <h1>Tìm kiếm video trên youtube</h1>
            <input type="text" name="themeSize" placeholder={input} onChange={getInput} onKeyPress={letSearch}></input>
            <h1>Kết quả cho từ khóa "{searchKey}"</h1>
            {/* <p>{result.items.map[0]}</p> */}
            {/* <p>{result.items[0]}</p> */}
            {result && <iframe className="player" type="text/html" width="800px" height="600px" src={"https://www.youtube.com/embed/"+result.items[0].id.videoId} frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen/>}
            {/* <ReactPlayer url='https://www.youtube.com/watch?v=ysz5S6PUM-U' /> */}
            <SearchResult>

            {loading && (<div>....Loading</div>)}
            {result && (

            <p>{result.items.map(item => (
                    // <li key={item.etag}>

                        <a href={"https://www.youtube.com/watch?v=" + item.id.videoId} key={item.etag}>
                        {/* <a href={"https://www.youtube.com/watch?v=" + item.id.videoId} key={item.etag}></a> */}
                            <Items>
                                <img src={item.snippet.thumbnails.default.url} alt="hình ảnh"/>
                                <p>{item.snippet.title}</p>
                            </Items>
                        </a>
                    // </li> 
                    ))}
            </p>
            )}
            </SearchResult>
        </div>

    );
}

export default App;
